/* implemention of functions */
const char* getPM_TypeName(int id) {
  return pm_type[id];
}

const char* getPM_NatureName(int id) {
  return pm_nature[id];
}

const char* getPM_MonsName(int id, int formid) {
  if (formid) {
    return pm_form_mons[getKeyByMonsForm(id, formid)];
  } else {
    return pm_monsname[id];
  }
}

const char* getPM_ItemName(int id) {
  return pm_item[id];
}

const char* getPM_MoveName(int id) {
  return pm_move[id];
}

const char* getPM_AbilityNameByFlag(int monsid, int formid, int abflag) {
  int abid_array[5] = {0,0,1,1,2};
  if (formid) {
    return pm_form_abflag[getKeyByMonsForm(monsid, formid)][abid_array[abflag]];
  } else {
    return pm_ability_flag[monsid][abid_array[abflag]];
  }
}

int getKeyByMonsForm(int monsid, int formid) {
  if (formid) {
    int i=0;
    for (i=0; i<MONS_FORM_MAX ;i++) {
      if (pm_key_mons[i] == monsid) {
        break;
        if (pm_key_form[i] == formid) {
        } else continue;
      } else continue;
    }
    return i;
  } else {
    return 0;
  }
}

